import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = ({apiKey}) => {
  const [topStocks, setTopStocks] = useState([]);

  useEffect(() => {
    const fetchTopStocks = async () => {
      try {
        const response = await axios.get(
          // Your Alpha Vantage API URL for top 5 rising stocks
          `https://www.alphavantage.co/query?function=SECTOR&apikey=${apikey}`
        );

        // Process the response to extract the top 5 rising stocks
        const topStocksData = response.data.bestMatches.slice(0, 5);

        setTopStocks(topStocksData);
      } catch (error) {
        console.error("Error fetching top stocks data:", error);
      }
    };

    fetchTopStocks();
  }, []);

  return (
    <div className="homeContainer">
      <div className="homeTopContainer">
        <div className="homeTitle"> Welcome to StockTracker </div>
      </div>
      <div className="homeContentContainer">
        <div className="top5Title">
          Here are the top 5 rising stocks for today!
        </div>
        <ul>
        {topStocks.map((stock, index) => (
          <li key={index}>{stock.symbol}</li>
        ))}
      </ul>
      </div>
    </div>
  );
};

export default Home;
